  let panelMenuItems = this.mContextTab.linkedTabPanel.getPanelSpecificMenuItems();
  for (let item of panelMenuItems) {
    aPopupMenu.insertBefore(item, tabSpecificEndSeparator);
    if (item.actionOnShowing) {
