  let hdrs = Services.prefs.getCharPref("mailnews.customHeaders");
  hdrs = hdrs.replace(/\s+/g, ""); // remove white spaces before splitting
  hdrsArray = hdrs.match(/[^:]+/g);
} catch (ex) {
}

let j = 0;
